from web.controller.admin import route_adminfrom common.libs.WebHelper import ops_renderfrom common.model.baoming import BaoMingfrom flask import request@route_admin.route('/index', methods=['GET', 'POST'])def admin_index():    resp = {'code': 200, 'totalCount': 0, 'pageTotal': 0, 'list': {}, 'curPage': 1}    req = request.values    page = req['page'] if 'page' in req else 1    totalCount = BaoMing.query.count()    offset = int(page) * 100    list = BaoMing.query.offset(offset).limit(100).all()    resp['totalCount'] = totalCount    resp['list'] = list    resp['pageTotal'] = (totalCount // 100)    resp['curPage'] = page    return ops_render('/admin/apply.html', resp)