from web.controller.admin import route_adminfrom common.libs.WebHelper import ops_renderfrom common.model.baoming import BaoMingfrom flask import request@route_admin.route('/index', methods=['GET', 'POST'])def admin_index():    resp = {'code': 200, 'totalCount': 0, 'pageTotal': 0, 'list': {}, 'curPage': 1}    req = request.values    page = req['page'] if 'page' in req else 1    f0 = req['f0'] if 'f0' in req else -1    f1 = req['f1'] if 'f1' in req else -1    f2 = req['f2'] if 'f2' in req else -1    f3 = req['f3'] if 'f3' in req else -1    f4 = req['f4'] if 'f4' in req else -1    query = BaoMing.query    offset = int(page - 1) * 100    if f0 != -1:        query = query.filter_by(f0=f0)    if f1 != 'null' and f1 != -1:        query = query.filter_by(f1=f1)    if f2 != 'null' and f2 != -1:        query = query.filter_by(f2=f2)    if f3 != 'null' and f3 != -1:        query = query.filter_by(f3=f3)    if f4 != 'null' and f4 != -1:        query = query.filter_by(f4=f4)    totalCount = query.count()    list = query.offset(offset).limit(100).all()    resp['totalCount'] = totalCount    resp['list'] = list    resp['pageTotal'] = (totalCount // 100)    resp['curPage'] = page    resp['f0'] = f0    resp['f1'] = f1    resp['f2'] = f2    resp['f3'] = f3    resp['f4'] = f4    return ops_render('/admin/apply.html', resp)